/* React file */

import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
//import Prisoner from './view/prisoner'

// Retourne 1 bouton
function Button(props){
  return(
      <button id={props.id} onClick={props.onClick}>
        {props.text}
      </button>
    );
}

 // Retourne 1 prisonier
function ReactPrisoner(props){
  return(
  <tr id={props.index}>
      <td>{props.name}</td>
      <td>{props.firstname}</td>
      <td>{props.dob}</td>
      <td>{props.prison_time}</td>
      <td>{props.motive}</td>
      <td><button className="delete_button" onClick={props.onClick}>X</button></td>
  </tr>
  );
}

class Prisoner extends React.Component{
    constructor(){
        super();
        this.state = {
            prisoners : [],
            prisoners_updated : false
        }
    }

    objectsEquals(o1, o2){
        return(Object.keys(o1).length === Object.keys(o2).length 
                && Object.keys(o1).every(p => o1[p] === o2[p]));
    }

    async componentDidUpdate(prevProps, prevState){
        if(!this.state.prisoners_updated){
            this.readPrisoner();
          }
    }

    async readPrisoner(){
        await fetch('/prisoners/read', {method: 'GET'}) // Envoi requete au serveur
              .then(res => res.json()) // Reponse de la requete
              .then((out) => { // Données a recuperer
                if(!this.objectsEquals(out, this.state.prisoners)){
                  this.setState({prisoners: out.result, prisoners_updated: true});
                }
              });
    }

    // Rendu quand on clique sur le bouton Prisoniers
    render(){
        let prisoners_array = [];
        this.state.prisoners.forEach((elt, i) => {
            prisoners_array.push(<ReactPrisoner index = {i} key = {i} name = {elt.name} firstname = "x" dob = "xx/xx/xxxx" prison_time = "xx days" motive = "x" onClick = {() => console.log(i)} />);
        });
        return (
            <table>
                <thead>
                    <tr>
                    <th>Nom</th>
                    <th>Prenom</th>
                    <th>Date Naissance</th>
                    <th>Temps prison.</th>
                    <th>Motif</th>
                    </tr>
                </thead>
                <tbody>
                    {prisoners_array}
                    <tr>
                    <td><input id="add_name" type="text" placeholder="Nom"></input></td>
                    <td><input id="add_firstname" type="text" placeholder="Prenom"></input></td>
                    <td><input id="add_dob" type="text" placeholder="xx/xx/xxxx"></input></td>
                    <td><input id="add_prison_time" type="text" placeholder="xx"></input> days</td>
                    <td><input id="add_motive" type="text" placeholder="motive"></input></td>
                    <td><button onClick={() => console.log('Ajouter')}>Ajouter</button></td>
                    </tr>
                </tbody>
            </table>
        );
    }
}
  
class App extends React.Component{
    constructor(){
      super();
      this.state = {
        page: "Accueil",
        //prisoners_updated: false,
        //prisoners: [],
        cases: []
      };
      
    }

    // Retoune le titre de la page + les boutons pour naviguer
    navbar(){
      return (
        <header>
          <h1>Prison de Nantes</h1>
          <div id="navbar">
              <Button key={0} id={"btnAccueil"} text={"Accueil"} onClick={() => {this.setState({page:"Accueil"}); console.log("Accueil")} }/>
              <Button key={1} id={"btnPrisoniers"} text={"Prisoniers"} onClick={() => {this.setState({page:"Prisoniers"}); console.log("Prisoniers");} }/>
              <Button key={2} id={"btnAffaires"} text={"Affaires"} onClick={() => {this.setState({page:"Affaires"}); console.log("Affaires")} }/>
          </div>
        </header>
      )
    }

    // Rendu quand on clique sur le bouton Accueil
    renderAccueil(){
      return (
        <div>
          {this.navbar()}
        </div>
      );
    }
    
    renderPrisoners(){
      return(
        <Prisoner />
      );
    }

    /*async readPrisoner(){
      await fetch('/prisoners/read', {method: 'GET'}) // Envoi requete au serveur
            .then(res => res.json()) // Reponse de la requete
            .then((out) => { // Données a recuperer
              if(!objectsEquals(out, this.state.prisoners)){
                this.setState({prisoners: out.result, prisoners_updated: true});
              }
            });
    }*/

    /*async addPrisoner(){
      if(document.getElementById("add_name").value !== ''){
        let new_name = document.getElementById("add_name").value;
        if(typeof new_name == 'string'){
          await fetch('/prisoners/create', 
          {
            method:'POST',
            headers:{"Content-Type": "application/json"}, 
            body:JSON.stringify({name:new_name}) 
          })
          .then((res) => {
              this.readPrisoner();
            }
          );
        }
      }
      else{
        console.log('clic addPrisoner');
      }
      
    }*/

    // Rendu quand on clique sur le bouton Affaires
    renderAffaires(){
      return (
        <div>
          {this.navbar()}
          <div id={this.state.page}>
          </div>
        </div>
      );
    }
  
    render(){
      if(this.state.page === 'Accueil'){
        return this.renderAccueil()
      }
      if(this.state.page === 'Prisoniers'){
        return (
          <div>
            {this.navbar}
            <div id={this.state.page}>
                {this.renderPrisoners()}
            </div>
          </div>
        );
      }
      if(this.state.page === 'Affaires'){
        return this.renderAffaires()
      }
    }

}
  
ReactDOM.render( <App />, document.getElementById('app') );
