/* React file */

import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import Prisoner from './view/prisoner'

// Retourne 1 bouton
function Button(props){
  return(
      <button id={props.id} onClick={props.onClick}>
        {props.text}
      </button>
    );
}
  
class App extends React.Component{
    constructor(){
      super();
      this.state = {
        page: "Accueil",
        //prisoners_updated: false,
        //prisoners: [],
        cases: []
      };
      
    }

    // Retoune le titre de la page + les boutons pour naviguer
    navbar(){
      return (
        <header>
          <h1>Prison de Nantes</h1>
          <div id="navbar">
              <Button key={0} id={"btnAccueil"} text={"Accueil"} onClick={() => {this.setState({page:"Accueil"}); console.log("Accueil")} }/>
              <Button key={1} id={"btnPrisoniers"} text={"Prisoniers"} onClick={() => {this.setState({page:"Prisoniers"}); console.log("Prisoniers");} }/>
              <Button key={2} id={"btnAffaires"} text={"Affaires"} onClick={() => {this.setState({page:"Affaires"}); console.log("Affaires")} }/>
          </div>
        </header>
      )
    }

    // Rendu quand on clique sur le bouton Accueil
    renderAccueil(){
      return (
        <div>
          {this.navbar()}
        </div>
      );
    }
    
    renderPrisoners(){
      return(
        Prisoner
      );
    }

    /*async readPrisoner(){
      await fetch('/prisoners/read', {method: 'GET'}) // Envoi requete au serveur
            .then(res => res.json()) // Reponse de la requete
            .then((out) => { // Données a recuperer
              if(!objectsEquals(out, this.state.prisoners)){
                this.setState({prisoners: out.result, prisoners_updated: true});
              }
            });
    }*/

    /*async addPrisoner(){
      if(document.getElementById("add_name").value !== ''){
        let new_name = document.getElementById("add_name").value;
        if(typeof new_name == 'string'){
          await fetch('/prisoners/create', 
          {
            method:'POST',
            headers:{"Content-Type": "application/json"}, 
            body:JSON.stringify({name:new_name}) 
          })
          .then((res) => {
              this.readPrisoner();
            }
          );
        }
      }
      else{
        console.log('clic addPrisoner');
      }
      
    }*/

    // Rendu quand on clique sur le bouton Affaires
    renderAffaires(){
      return (
        <div>
          {this.navbar()}
          <div id={this.state.page}>
          </div>
        </div>
      );
    }
  
    render(){
      if(this.state.page === 'Accueil'){
        return this.renderAccueil()
      }
      if(this.state.page === 'Prisoniers'){
        return (
          <div>
            {this.navbar}
            <div id={this.state.page}>
                {this.renderPrisoners()}
            </div>
          </div>
        );
      }
      if(this.state.page === 'Affaires'){
        return this.renderAffaires()
      }
    }

}
  
ReactDOM.render( <App />, document.getElementById('app') );
