import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

function Prisoner(props){
    return(
      <li>
        <p>{props.name}</p>
        <button className="delete_button" onClick={props.onClick}>X</button>
      </li>
    );
  }
  
  class App extends React.Component{
    constructor(){
      super();
      this.state = {
        prisoners: []
      };
    }
  
    //componentDidMount(){
      //this.read();
    //}

    renderPrisoner(name){
      return <Prisoner
                name = {name}
                onClick = {() => console.log('coucou X')}
            />;
    }
  
    display(){
      if( this.state.prisoners.array !== undefined ){
        this.state.prisoners.array.forEach(element => {
          this.renderPrisoner(element.name);
        });
      }
      
    }

    read(){
      fetch('http://localhost:8080/prisoners/read', {method: 'GET'}) // Envoi requete au serveur
      .then(res => res.json()) // Reponse de la requete
      .then((out) => { // Données a recuperer
        this.setState({prisoners: out.result});
      });
    }

  render(){
    return(
      <ul> 
        {this.display()}
      </ul>
    );
  }

}
  
  ReactDOM.render( <App />, document.getElementById('prisoners') );
