import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

function Button(props){
  return(
      <button id={props.id} onClick={props.onClick}>
        {props.text}
      </button>
    );
}

function Prisoner(props){
    return(
      <li id={props.index}>
        <p>{props.name}</p>
        <button className="delete_button" onClick={props.onClick}>X</button>
      </li>
    );
  }
  
  class App extends React.Component{
    constructor(){
      super();
      this.state = {
        page: "Accueil",
        prisoners: [],
        affaires: []
      };
      
    }

    navbar(){
      return (
        <div>
          <h1>{this.state.page}</h1>
          <div id="navbar">
              <Button key={0} id={"btnAccueil"} text={"Accueil"} onClick={() => text}/>;
          </div>
        </div>
      )
    }

    // Fonction de la classe React.Component, ne s'execute qu'une fois a chaque rendu
    async componentDidMount(){
      switch(this.state.page){
        case 'Accueil':
          break;
        case 'Prisoniers':
          await fetch('http://localhost:8080/prisoners/read', {method: 'GET'}) // Envoi requete au serveur
          .then(res => res.json()) // Reponse de la requete
          .then((out) => { // Données a recuperer
            this.setState({prisoners: out.result});
          });
          break;
        case 'Affaires':
          break;
        default:
          break;
      }
    }
  
    renderPrisoner(name, i){
      return <Prisoner
                index = {i}
                key = {i}
                name = {name}
                onClick = {() => console.log(i)}
            />;
    }
  
    render(){
      return(
        <div>
          {this.navbar()}
          <div id={this.page}>
            <ul> 
              {
                this.state.prisoners.map((elt, i) => {
                  return this.renderPrisoner(elt.name, i);
                })
              }
            </ul>
          </div>
        </div>  
      );
    }

  }
  
  ReactDOM.render( <App />, document.getElementById('app') );
