import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

function Prisoner(props){
    return(
      <li>
        <p>{props.name}</p>
        <button className="delete_button" onClick={props.onClick}>X</button>
      </li>
    );
  }
  
  class App extends React.Component{
    constructor(){
      super();
      this.state = {
        prisoners: []
      };
    }

    async componentDidMount(){
      await fetch('http://localhost:8080/prisoners/read', {method: 'GET'}) // Envoi requete au serveur
      .then(res => res.json()) // Reponse de la requete
      .then((out) => { // Données a recuperer
        this.setState({prisoners: out.result});
      });
    }
  
    renderPrisoner(name, i){
      return <Prisoner
                key = {i}
                name = {name}
                onClick = {() => console.log('coucou X')}
            />;
    }
  
    render(){
      return(
        <ul> 
          {
          this.state.prisoners.map((elt, i) => {
            return this.renderPrisoner(elt.name, i);
          })
          }
        </ul>
      );
    }

    read(){
      this.state.prisoners.map((elt, i) => {
        console.log(elt.name);
        return this.renderPrisoner(elt.name);
      });
    }
    



  }
  
  ReactDOM.render( <App />, document.getElementById('prisoners') );
